src/utils/getElapsedTime.js.content:
const getElapsedTime = (curr) => {
  const now = new Date();

  const epoch = new Date(curr);

  const difference = now - epoch;

  let seconds = difference / 1000;

  return formatTime(Math.floor(seconds));
};

const getPercentage = (data) => {
  let tot = Math.ceil((data.timestamps.end - data.timestamps.start) / 1000);
  let now = new Date();
  let curr = Math.ceil((now - data.timestamps.start) / 1000);
  let perc = Math.ceil((curr * 100) / tot);
  if (perc >= 100) return 100;
  return perc;
};

const formatTime = (seconds) => {
  if (isNaN(seconds) || seconds < 0) {
    return "Invalid time";
  }

  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const remainingSeconds = seconds % 60;
  const formattedTime = `${hours === 0 ? "" : hours + "h :"} ${
    minutes === 0 && hours === 0 ? "" : minutes + "m :"
  } ${remainingSeconds + "s"}`;

  return formattedTime;
};

export { getElapsedTime, formatTime, getPercentage };

src/Layout/Layout.js.content:
import React, { useEffect, useState } from "react";
import Aos from "aos";
import "aos/dist/aos.css";
import "../App.css";

export default function Layout({ children }) {
  const [isDesktop, setIsDesktop] = useState(false);

  useEffect(() => {
    const checkDevice = () => {
      const hasHover = window.matchMedia('(hover: hover)').matches;
      const isLargeScreen = window.innerWidth >= 1024;
      setIsDesktop(hasHover && isLargeScreen);
    };

    checkDevice();
    window.addEventListener('resize', checkDevice);

    return () => window.removeEventListener('resize', checkDevice);
  }, []);

  useEffect(() => {
    if (!isDesktop) return;

    const cursor = document.querySelector(".cursor");
    if (!cursor) return;

    const handleMouseMove = (e) => {
      cursor.style.left = e.clientX + "px";
      cursor.style.top = e.clientY + "px";
    };

    const handleMouseDown = () => {
      cursor.style.height = "30px";
      cursor.style.width = "30px";
    };

    const handleMouseUp = () => {
      cursor.style.height = "20px";
      cursor.style.width = "20px";
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("mousedown", handleMouseDown);
    window.addEventListener("mouseup", handleMouseUp);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mousedown", handleMouseDown);
      window.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDesktop]);

  useEffect(() => {
    Aos.init({ 
      duration: 800,
      once: true,
      easing: 'ease-out-cubic'
    });
  }, []);

  return (
    <div className="min-h-screen gradient-bg bg-grid relative overflow-x-hidden">
      <div className="floating-shapes" />
      <div className="relative z-10">
        {children}
      </div>
      {isDesktop && <div className="cursor" />}
    </div>
  );
}

src/components/Navigation.js.content:
import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import GitHubIcon from "@mui/icons-material/GitHub";
import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";

export default function Navigation() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeSection, setActiveSection] = useState("home");
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);

      const sections = ["home", "about", "resume", "projects", "contact"];
      const scrollPosition = window.scrollY + 100;

      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const top = element.offsetTop;
          const height = element.offsetHeight;
          if (scrollPosition >= top && scrollPosition < top + height) {
            setActiveSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    if (isMobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isMobileMenuOpen]);

  const scrollToSection = (sectionId) => {
    setIsMobileMenuOpen(false);
    if (sectionId === "home") {
      window.scrollTo({ top: 0, behavior: "smooth" });
    } else {
      const element = document.getElementById(sectionId);
      if (element) {
        element.scrollIntoView({ behavior: "smooth" });
      }
    }
  };

  const navItems = [
    { id: "home", label: "Home" },
    { id: "about", label: "About" },
    { id: "resume", label: "Resume" },
    { id: "projects", label: "Projects" },
    { id: "contact", label: "Contact" },
  ];

  return (
    <>
      <nav
        className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
          isScrolled
            ? "bg-dark-900/90 backdrop-blur-xl border-b border-dark-700/50 py-4"
            : "bg-transparent py-6"
        }`}
      >
        <div className="max-w-7xl mx-auto px-6 flex items-center justify-between">
          <NavLink
            to="/"
            onClick={() => scrollToSection("home")}
            className="flex items-center gap-3 group"
          >
            <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-primary-500 to-accent-purple flex items-center justify-center text-white font-bold text-lg">
              G
            </div>
            <span className="text-xl font-display font-semibold text-white group-hover:text-primary-400 transition-colors">
              Gunjan<span className="text-primary-400">.</span>
            </span>
          </NavLink>

          <div className="hidden md:flex items-center gap-8">
            {navItems.map((item) => (
              <button
                key={item.id}
                onClick={() => scrollToSection(item.id)}
                className={`nav-link ${activeSection === item.id ? "active text-primary-400" : ""}`}
              >
                {item.label}
              </button>
            ))}
          </div>

          <div className="hidden md:flex items-center gap-4">
            <a
              href="https://github.com/gun03-hub"
              target="_blank"
              rel="noopener noreferrer"
              className="p-2 text-dark-400 hover:text-primary-400 transition-colors"
              title="GitHub"
            >
              <GitHubIcon />
            </a>
            <a
              href="https://www.linkedin.com/in/gunjan-arora-4248462a9/"
              target="_blank"
              rel="noopener noreferrer"
              className="p-2 text-dark-400 hover:text-primary-400 transition-colors"
              title="LinkedIn"
            >
              <LinkedInIcon />
            </a>
            <button
              onClick={() => scrollToSection("contact")}
              className="btn-primary text-sm"
            >
              Let's Talk
            </button>
          </div>

          <button
            className="md:hidden p-2 text-white z-50"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            aria-label={isMobileMenuOpen ? "Close menu" : "Open menu"}
          >
            {isMobileMenuOpen ? <CloseIcon /> : <MenuIcon />}
          </button>
        </div>
      </nav>

      {isMobileMenuOpen && (
        <div 
          className="md:hidden fixed inset-0 z-40 bg-dark-950/95 backdrop-blur-xl"
          onClick={() => setIsMobileMenuOpen(false)}
        >
          <div 
            className="flex flex-col items-center justify-center h-full gap-8"
            onClick={(e) => e.stopPropagation()}
          >
            {navItems.map((item, index) => (
              <button
                key={item.id}
                onClick={() => scrollToSection(item.id)}
                className={`text-2xl font-display font-semibold transition-all duration-300 ${
                  activeSection === item.id
                    ? "text-primary-400"
                    : "text-white hover:text-primary-400"
                }`}
                style={{ animationDelay: `${index * 50}ms` }}
              >
                {item.label}
              </button>
            ))}

            <div className="flex items-center gap-6 mt-8">
              <a
                href="https://github.com/gun03-hub"
                target="_blank"
                rel="noopener noreferrer"
                className="p-3 text-dark-300 hover:text-primary-400 transition-colors"
              >
                <GitHubIcon className="text-3xl" />
              </a>
              <a
                href="https://www.linkedin.com/in/gunjan-arora-4248462a9/"
                target="_blank"
                rel="noopener noreferrer"
                className="p-3 text-dark-300 hover:text-primary-400 transition-colors"
              >
                <LinkedInIcon className="text-3xl" />
              </a>
            </div>

            <button
              onClick={() => scrollToSection("contact")}
              className="btn-primary mt-4"
            >
              Let's Talk
            </button>
          </div>
        </div>
      )}
    </>
  );
}

