@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&family=Poppins:wght@400;500;600;700;800&family=JetBrains+Mono:wght@400;500&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    scroll-behavior: smooth;
  }

  body {
    @apply bg-dark-950 text-white antialiased;
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
  }

  ::selection {
    @apply bg-primary-500/30 text-white;
  }

  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-dark-900;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-primary-600/50 rounded-full;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-primary-500;
  }
}

@layer components {
  .section-heading {
    @apply text-4xl md:text-5xl lg:text-6xl font-bold font-display bg-gradient-to-r from-white via-primary-200 to-primary-400 bg-clip-text text-transparent;
  }

  .section-subheading {
    @apply text-lg md:text-xl text-dark-300 max-w-2xl;
  }

  .glass-card {
    @apply bg-dark-800/50 backdrop-blur-xl border border-dark-700/50 rounded-2xl;
  }

  .gradient-border {
    @apply relative;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(139, 92, 246, 0.2));
  }

  .gradient-border::before {
    content: '';
    @apply absolute inset-0 rounded-2xl;
    padding: 1px;
    background: linear-gradient(135deg, #3b82f6, #8b5cf6);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  .btn-primary {
    @apply px-8 py-3 bg-gradient-to-r from-primary-600 to-primary-500 text-white font-semibold rounded-xl 
           hover:from-primary-500 hover:to-primary-400 transform hover:-translate-y-0.5 
           transition-all duration-300 shadow-lg hover:shadow-primary-500/25;
  }

  .btn-outline {
    @apply px-8 py-3 border-2 border-primary-500/50 text-primary-400 font-semibold rounded-xl
           hover:bg-primary-500/10 hover:border-primary-400 transform hover:-translate-y-0.5
           transition-all duration-300;
  }

  .nav-link {
    @apply relative text-dark-300 hover:text-white transition-colors duration-300 font-medium;
  }

  .nav-link::after {
    content: '';
    @apply absolute bottom-0 left-0 w-0 h-0.5 bg-primary-500 transition-all duration-300;
  }

  .nav-link:hover::after,
  .nav-link.active::after {
    @apply w-full;
  }

  .nav-link.active {
    @apply text-primary-400;
  }

  .skill-bar {
    @apply h-2 rounded-full bg-dark-700 overflow-hidden;
  }

  .skill-bar-fill {
    @apply h-full rounded-full bg-gradient-to-r from-primary-500 to-accent-cyan transition-all duration-1000 ease-out;
  }

  .project-card {
    @apply glass-card overflow-hidden hover:border-primary-500/30 transition-all duration-500;
  }

  .project-card:hover {
    @apply shadow-card-hover;
    transform: translateY(-8px);
  }

  .tech-tag {
    @apply px-3 py-1 text-xs font-medium rounded-full bg-primary-500/10 text-primary-300 border border-primary-500/20;
  }
}

@layer utilities {
  .text-gradient {
    @apply bg-gradient-to-r from-primary-400 via-accent-cyan to-primary-400 bg-clip-text text-transparent;
  }

  .text-gradient-purple {
    @apply bg-gradient-to-r from-primary-400 to-accent-purple bg-clip-text text-transparent;
  }

  .bg-grid {
    background-image: 
      linear-gradient(rgba(59, 130, 246, 0.03) 1px, transparent 1px),
      linear-gradient(90deg, rgba(59, 130, 246, 0.03) 1px, transparent 1px);
    background-size: 50px 50px;
  }

  .bg-noise {
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.03'/%3E%3C/svg%3E");
  }
}

